define([], function () {
  return function (runtime, params, callback) {

    var webMode = params.options['web-mode'];

    if (!params.deploy.flags.cordova &&
        webMode !== 'none') {

      var splash = '';
      var css = '';

      if (webMode === 'spinner') {

        // Spinner from sleek
        splash = "<div class='_joshfire_factory_splashscreen' style='height:100%;width:100%;top:0;left:0;position:absolute;z-index:100000;background: #000 url(data:image/gif;base64,) no-repeat center center;";
        splash += "'>&nbsp;</div>";

      } else if (webMode === 'text') {

        splash = "<div class='_joshfire_factory_splashscreen' style='height:100%;width:100%;top:0;left:0;position:absolute;z-index:100000;background: #000;'><div style='z-index:100000;color:#FFF;background:#000;position:absolute;top:50%;width:100%;text-align:center;font-family:Arial,Helvetica,sans-serif;'>Loading...</div></div>";

      } else if (webMode === 'factory') {

        // cat path/to/file.png | openssl base64 | tr -d '\n' | pbcopy

        css = "<style type='text/css'>" +

          "._joshfire_factory_splashscreen {height:100%;width:100%;top:0;left:0;position:absolute;z-index:100000;background: #5A5B5F;opacity:1;" +
            "-webkit-transition: opacity .5s linear;" +
            "-moz-transition: opacity .5s linear;" +
            "-o-transition: opacity .5s linear;" +
            "transition: opacity .5s linear;" +
            "background: -webkit-radial-gradient(center 40%, circle cover,  rgba(90,91,95,1) 0%,rgba(78,79,83,1) 100%);" + /* Chrome10+,Safari5.1+ */
            "background: -webkit-gradient(radial, center 40%, 0px, center center, 100%, color-stop(0%,rgba(90,91,95,1)), color-stop(100%,rgba(78,79,83,1)));" + /* Chrome,Safari4+ */
            "background: -moz-radial-gradient(center 40%, circle cover,  rgba(90,91,95,1) 0%, rgba(78,79,83,1) 100%);" + /* FF3.6+ */
            "background: -o-radial-gradient(center 40%, circle cover,  rgba(90,91,95,1) 0%,rgba(78,79,83,1) 100%);" + /* Opera 12+ */
            "background: -ms-radial-gradient(center 40%, circle cover,  rgba(90,91,95,1) 0%,rgba(78,79,83,1) 100%);" + /* IE10+ */
            "background: radial-gradient(circle at center 40%,  rgba(90,91,95,1) 0%,rgba(78,79,83,1) 100%);" + /* W3C */
            "filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#5a5b5f', endColorstr='#4e4f53',GradientType=1 );" + /* IE6-9 fallback on horizontal gradient */
          "}" +

          "._joshfire_factory_splashscreen_spinner {position: absolute;top: 70%;right: 0;left: 0;margin: 0 auto;background-repeat: no-repeat;background-position: 0 0;width: 32px;height: 32px;margin-top: -16px;" +
            "background-image:url(data:image/gif;base64,);" +
          "}";

        if (params.config.app.integration === 'dailymotion') {

          css +=

            // &
            "._joshfire_factory_splashscreen_and {color:#DDD;font-family:'Helvetica Neue Light','Helvetica Neue',arial,verdana,helvetica;font-weight:lighter;sans-serif;font-size:40px;text-align:center;position:absolute;width:100%;height:165px;top:25%;line-height:165px;}" +

            "._joshfire_factory_splashscreen_factory {position:absolute;top:25%;background-repeat:no-repeat;background-position:left;background-size:contain;margin:0 auto;width:30%;right:15%;height:165px;" +
              "background-image:url(data:image/png;base64,);" +
            "}" +

            //DM
            "._joshfire_factory_splashscreen_icon {position:absolute;top:25%;background-repeat:no-repeat;background-position:right;background-size:contain;margin:0 auto;width:30%;left:15%;height:165px;" +
              "background-image:url(data:image/png;base64,);" +
            "}";

        } else {

          css +=

            "._joshfire_factory_splashscreen_icon {position:absolute;left:0;right:0;background-repeat:no-repeat;background-position:0 0;background-size:contain;margin:0 auto;" +
              "background-image:url(data:image/png;base64,);" +
              "width:133px;height:124px;top:40%;margin-top:-48px;" +
            "}" +

            "._joshfire_factory_splashscreen_factory {position: absolute;background-repeat: no-repeat;background-position: 0 0;background-size: cover;width:131px;height: 29px;bottom: 10px;right: 15px;" +
              "background-image:url(data:image/png;base64,);" +
            "}";

        }

        css += '</style>';

        params.content = runtime.headAppend(params.content, css);

        // Disable web splashscreen if we're in standalone mode
        // Not anymore https://www.pivotaltracker.com/story/show/34205347
        // splash = "<script type='text/javascript'>if (('standalone' in window.navigator) && window.navigator.standalone) document.write('<style>._joshfire_factory_splashscreen{display:none;}</style>');</script>";

        if (params.config.app.integration === 'dailymotion') {
          splash += "<div class='_joshfire_factory_splashscreen'><div class='_joshfire_factory_splashscreen_and'>&amp;</div><div class='_joshfire_factory_splashscreen_icon'></div><div class='_joshfire_factory_splashscreen_spinner'></div><div class='_joshfire_factory_splashscreen_factory'></div></div>";
        } else {
          splash += "<div class='_joshfire_factory_splashscreen'><div class='_joshfire_factory_splashscreen_icon'></div><div class='_joshfire_factory_splashscreen_spinner'></div><div class='_joshfire_factory_splashscreen_factory'></div></div>";
        }

      } else if (webMode === 'custom') {
        var imgObject = params.options['web-mode-splashscreen'];
        var imgURL = imgObject ? imgObject.contentURL : null;

        if (imgURL) {
          css = '<style type=\'text/css\'>' +

            '#splashscreen {' +
              'background-image: url(' + imgURL + ');' +
              'background-size: cover;' +
            '}';

          css += '</style>';

          params.content = runtime.headAppend(params.content, css);
        }

      }

      params.content = runtime.bodyPrepend(params.content, splash);

    }

    callback();
  };
});